SELECT as_nombre, es_apellidos, es_nombres, us_titulo, us_fullname, pe_nombre, ap_nombre, ru_nombre, re_calificacion FROM sw_rubrica_estudiante r, sw_asignatura a, sw_estudiante e, sw_paralelo_asignatura pa, sw_usuario u, sw_periodo_evaluacion pe, sw_aporte_evaluacion ap, sw_rubrica_evaluacion ru WHERE r.id_paralelo = pa.id_paralelo and r.id_asignatura = pa.id_asignatura and r.id_asignatura = a.id_asignatura and pa.id_usuario = u.id_usuario and r.id_rubrica_personalizada = ru.id_rubrica_evaluacion and ap.id_aporte_evaluacion = ru.id_aporte_evaluacion and pe.id_periodo_evaluacion = ap.id_periodo_evaluacion and r.id_estudiante = e.id_estudiante and pe.id_periodo_evaluacion = 1 and re_calificacion > 10 ORDER BY us_fullname, as_nombre